import React from "react";
import { compose, withProps } from "recompose";
import "./mapWithMarkers.scss";
import MarkerIconActive from "../../images/markerIconActive.png";
import MarkerIconInactive from "../../images/markerIconInactive.png";
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker
} from "react-google-maps";
import { withRouter } from "react-router-dom";

const defaultMapOptions = {
  zoomControl: true,
  mapTypeControl: false,
  scaleControl: false,
  streetViewControl: false,
  rotateControl: false,
  fullscreenControl: false,
  clickableIcons: false
};

const MapWithMarkers = compose(
  withProps({
    googleMapURL:
      "https://maps.googleapis.com/maps/api/js?key=AIzaSyBvaYrU3bnm60a1X4gyhULxiO-5gqhVi48&libraries=geometry,drawing,places",
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `100%` }} />,
    mapElement: <div style={{ height: `100%` }} />
  }),
  withScriptjs,
  withGoogleMap
)(props => (
  <GoogleMap
    options={defaultMapOptions}
    defaultZoom={13}
    defaultCenter={{ lat: 42.357064, lng: -71.062577 }}
  >
    {props.listings.map(listing => {
      return (
        <DetailedMarker position={listing.position} content={listing.content} />
      );
    })}
    <div className="floating-panel">asdf</div>
  </GoogleMap>
));

const displayMarker = listing => {
  console.log(listing.content);
  return (
    <DetailedMarker position={listing.position} content={listing.content} />
  );
};

class DetailedMarker extends React.Component {
  constructor() {
    super();
    this.state = {
      markerIcon: MarkerIconInactive,
      isSelected: false,
      label: "$344"
    };
    this.onToggleSelection = this.onToggleSelection.bind(this);
    this.onMouseOverMarker = this.onMouseOverMarker.bind(this);
    this.onMouseOutMarker = this.onMouseOutMarker.bind(this);
  }

  resetMarker() {
    this.setState({
      markerIcon: MarkerIconInactive,
      isSelected: false
    });
  }

  onToggleSelection() {
    this.setState({
      isSelected: !this.state.isSelected
    });
    if (this.state.isSelected) {
      this.onMouseOverMarker();
    } else {
      this.onMouseOutMarker();
    }
  }

  onMouseOverMarker() {
    if (!this.state.isSelected) {
      this.setState({
        markerIcon: MarkerIconActive
      });
    }
  }

  onMouseOutMarker() {
    if (!this.state.isSelected) {
      this.setState({
        markerIcon: MarkerIconInactive
      });
    }
  }

  render() {
    return (
      <Marker
        position={this.props.position}
        onClick={this.onToggleSelection}
        label={{
          text: this.state.label,
          color: "#FFFFFF",
          fontFamily: "Avenir",
          fontSize: "14px"
        }}
        onMouseOver={this.onMouseOverMarker}
        onMouseOut={this.onMouseOutMarker}
        options={{ icon: this.state.markerIcon, fontSize: "300px" }}
        fontSize="300px"
      ></Marker>
    );
  }
}

export default MapWithMarkers;

