import React from "react";
import "./App.scss";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { LandingPage } from "./components/landing-page/landing-page-index";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      view: "LandingPage",
      login: true,
      userInfo: null
    };
    this.changeView = this.changeView.bind(this);
    this.login = this.login.bind(this);
  }

  changeView(view) {
    this.setState(() => {
      view: view;
    });
  }

  login(view, res) {
    this.setState(() => {
      userInfo: res;
    });
    this.changeView(view);
  }

  render() {
    return (
      <Router>
        {this.state.view == "LandingPage" && (
          <div className="App">
            <LandingPage changeView={this.login}></LandingPage>
            <Route path="/register" component={LandingPage}></Route>
            <Route path="/login" component={LandingPage}></Route>
          </div>
        )}
      </Router>
    );
  }
}

export default App;
