import React from "react";
import { compose, withProps } from "recompose";
import "bootstrap/dist/css/bootstrap.min.css";
import "./mapWithMarkers.scss";
import { DetailedMarker } from "./marker/detailedMarker.jsx";
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker
} from "react-google-maps";
import { withRouter } from "react-router-dom";
import MarkerIconActive from "../../images/markerIconActive.png";
import MarkerIconInactive from "../../images/markerIconInactive.png";

const defaultMapOptions = {
  zoomControl: true,
  mapTypeControl: false,
  scaleControl: false,
  streetViewControl: false,
  rotateControl: false,
  fullscreenControl: false,
  clickableIcons: false
};

let selectedPicture;
let selectedTitle;
let selectedPrice;

const MapWithMarkers = compose(
  withProps({
    googleMapURL:
      "https://maps.googleapis.com/maps/api/js?key=AIzaSyBvaYrU3bnm60a1X4gyhULxiO-5gqhVi48&libraries=geometry,drawing,places",
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `100%` }} />,
    mapElement: <div style={{ height: `100%` }} />
  }),
  withScriptjs,
  withGoogleMap
)(props => (
  <GoogleMap
    options={defaultMapOptions}
    defaultZoom={13}
    defaultCenter={{ lat: 42.397064, lng: -71.092577 }}
  >
    {props.listings.map(listing => {
      if (listing.listingId == props.selectedMarker) {
        selectedTitle = listing.title;
        selectedPrice = listing.price;
      }
      console.log(listing.listingId);
      console.log(props.selectedMarker);
      return (
        <DetailedMarker
          position={listing.position}
          listingId={listing.listingId}
          price={listing.price}
          isSelected={listing.listingId == props.selectedMarker ? true : false}
          setSelectedMarker={props.setSelectedMarker}
        />
      );
    })}
    <div className="floating-panel">
      <div className="row no-gutters">
        <div className="col-lg-7">hello</div>
        <div className="col-lg-5">
          <div className="">
            <label>{selectedTitle}</label>
          </div>
          <div className="">
            <label>{selectedPrice}</label>
          </div>
        </div>
      </div>
    </div>
  </GoogleMap>
));

export default MapWithMarkers;
