import React from "react";
import "./App.scss";
import {
  BrowserRouter as Router,
  Route,
  Link,
  Redirect
} from "react-router-dom";
import { LandingPage } from "./components/landing-page/landing-page-index";
import { Profile } from "./components/profile/profile-index";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      login: false,

      //user info
      userInfo: ""
    };
  }

  login(res) {
    this.setState({
      userInfo: res.data
    });
    return <Redirect to="/login" />;
  }

  render() {
    return (
      <Router>
        <div className="App">
          <Route
            exact
            path="/"
            render={() => <LandingPage login={this.login} />}
          ></Route>
          <Route
            path="/profile"
            render={() => <Profile userInfo={this.state.userInfo} />}
          ></Route>
        </div>
      </Router>
    );
  }
}

export default App;
