import React from "react";
import "./App.scss";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { LandingPage } from "./components/landing-page/landing-page-index";
import { Profile } from "./components/profile/profile-index";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      view: "LandingPage", //default LandingPage
      login: true,

      //user info
      userSessionKey: null
    };
    this.changeView = this.changeView.bind(this);
    this.login = this.login.bind(this);
  }

  componentDidMount() {
    try {
      const sessionId = localStorage.getItem("sessionId");
      const username = localStorage.getItem("username");
      const view = localStorage.getItem("view");
      const userSessionKey = {
        sessionId: sessionId,
        username: username
      };
      if (view == null) {
        this.setState({
          view: "LandingPage"
        });
      } else {
        this.setState({
          userSessionKey: userSessionKey,
          view: view
        });
      }
    } catch (e) {
      console.log(e);
    }
    console.log("compoenent mounted");
  }

  changeView(view) {
    this.setState({
      view: view
    });
    localStorage.setItem("view", view);
  }

  login(view, res) {
    console.log(res);
    this.setState({
      userSessionKey: res.data
    });
    localStorage.setItem("sessionId", res.data.sessionId);
    localStorage.setItem("username", res.data.username);
    this.changeView(view);
  }

  render() {
    return (
      <Router>
        <div className="App">
          {this.state.view == "LandingPage" && (
            <div>
              <LandingPage login={this.login} />
            </div>
          )}
          {this.state.view == "profile" && (
            <div>
              <Profile userInfo={(this.userSessionKey, this.changeView)} />
            </div>
          )}
        </div>
      </Router>
    );
  }
}

export default App;
