import React from "react";
import { Map, GoogleApiWrapper, InfoWindow, Marker } from "google-maps-react";
import { Marking } from "./marking/marking-index";
import "./mapContainer.scss";
const mapStyles = {
  width: "100%",
  height: "100%",
  overflow: "hidden"
};

export class MapContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showingInfoWindow: false, //Hides or the shows the infoWindow
      activeMarker: {}, //Shows the active marker upon click
      selectedPlace: {},
      stores: [
        { lat: 43.4717248, lng: -80.5329432 },
        { lat: 43.4728907, lng: -80.5329043 },
        { lat: 43.4734552, lng: -80.5344815 },
        { lat: 43.4763211, lng: -80.5361521 }
      ]
    };
  }

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return (
        <Marker
          key={index}
          id={index}
          position={{
            lat: store.lat,
            lng: store.lng
          }}
          onClick={() => console.log("You clicked me!")}
        />
      );
    });
  };

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  render() {
    return (
      <Map
        google={this.props.google}
        zoom={14}
        style={mapStyles}
        initialCenter={{ lat: 43.468, lng: -80.5373 }}
      >
        {/* <Marker onClick={this.onMarkerClick} name={"181 Lester"} />
        <InfoWindow
          marker={this.state.activeMarker}
          visible={this.state.showingInfoWindow}
          onClose={this.onClose}
        >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
        </InfoWindow> */}
        {this.displayMarkers()}
      </Map>
    );
  }
}

MapContainer = GoogleApiWrapper({
  apiKey: "AIzaSyBvaYrU3bnm60a1X4gyhULxiO-5gqhVi48"
})(MapContainer);
