import React from "react";
import "./App.scss";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { LandingPage } from "./components/landing-page/landing-page-index";
import { Profile } from "./components/profile/profile-index";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      view: "LandingPage", //default LandingPage
      login: false,

      //user info
      userInfo: null
    };
    this.changeView = this.changeView.bind(this);
    this.login = this.login.bind(this);
  }

  changeView(view) {
    this.setState({
      view: view
    });
  }

  login(view, res) {
    this.setState({
      userInfo: res.data
    });
    this.changeView(view);
  }

  render() {
    return (
      <Router>
        <div className="App">
          <Route
            path="/landingpage"
            render={() => <LandingPage login={this.login} />}
          ></Route>
          <Route
            path="/profile"
            render={() => <Profile userInfo={this.state.userInfo} />}
          ></Route>
        </div>
      </Router>
    );
  }
}

export default App;
