import React from "react";
import { compose, withProps } from "recompose";
import "./mapWithMarkers.scss";
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
  InfoWindow
} from "react-google-maps";

const defaultMapOptions = {
  zoomControl: true,
  mapTypeControl: false,
  scaleControl: false,
  streetViewControl: false,
  rotateControl: false,
  fullscreenControl: false,
  clickableIcons: false
};

const MapWithMarkers = compose(
  withProps({
    googleMapURL:
      "https://maps.googleapis.com/maps/api/js?key=AIzaSyBvaYrU3bnm60a1X4gyhULxiO-5gqhVi48&libraries=geometry,drawing,places",
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `100%` }} />,
    mapElement: <div style={{ height: `100%` }} />
  }),
  withScriptjs,
  withGoogleMap
)(props => (
  <GoogleMap
    options={defaultMapOptions}
    defaultZoom={13}
    defaultCenter={{ lat: 42.357064, lng: -71.062577 }}
  >
    {props.listings.map(listing => {
      return (
        <MarkerWithInfoWindow
          position={listing.position}
          content={listing.content}
        />
      );
    })}
    <div className="floating-panel">asdf</div>
  </GoogleMap>
));

const displayMarker = listing => {
  console.log(listing.content);
  return (
    <MarkerWithInfoWindow
      position={listing.position}
      content={listing.content}
    />
  );
};

class MarkerWithInfoWindow extends React.Component {
  constructor() {
    super();
    this.state = {
      isSelected: false
    };
    this.onToggleSelection = this.onToggleSelection.bind(this);
  }

  onToggleSelection() {
    this.setState({
      isSelected: !this.state.isSelected
    });
    if (this.state.isSelected === true) {
      this.setState({
        color: "blue"
      });
    }
  }

  render() {
    return (
      <Marker position={this.props.position} onClick={this.onToggleSelection}>
        <InfoWindow
          onCloseClick={this.onToggleSelection}
          style={{ background: this.state.color }}
        >
          <h3 style={{ background: this.state.color }}>{this.props.content}</h3>
        </InfoWindow>
      </Marker>
    );
  }
}

export default MapWithMarkers;

