  constructor(props) {
    super(props);
    this.state = {
      listings: [
        {
          listingId: "1",
          position: { lat: 42.3875968, lng: -71.0994968 },
          content: "$233",
          selected: false
        },
        {
          listingId: "2",
          position: { lat: 42.3974868, lng: -71.1124943 },
          content: "$753",
          selected: false
        },
        {
          listingId: "3",
          position: { lat: 42.4074868, lng: -71.1224943 },
          content: "$600",
          selected: false
        }
      ]
    };
    this.resetMarkers = this.resetMarkers.bind(this);
  }

  resetMarkers(listingId) {
    for (let i = 0; i < this.state.listings.length; i++) {
      if (this.state.listings[i].listingId != listingId) {
        this.state.listings[i].selected = false;
        console.log(listingId);
      } else {
        this.state.listings[i].selected = true;
        console.log(listingId);
      }
    }
  }

